"use strict";const load=()=>{document.getElementById("js-more-bio-btn").onclick=function(e){const t=document.getElementById("js-bio-wrapper");t.classList.contains("hidden")?(e.target.innerText="Hide",t.style.height="450px"):(e.target.innerText="More...",t.style.height="0");t.classList.toggle("hidden"),t.classList.toggle("displayed"),e.target.classList.toggle("displayed")}};document.addEventListener("DOMContentLoaded",load);